   def print_candidates(self, dict_pos={}, itera=0):
      pos = ''
      line = '-----------------------------------------------------------------------------------------'

      for key, value in dict_pos.items():
         pos += '|   '+key+': ' + ', '.join(str(v) for v in value) + '   |\n '
         pos += line+'\n'

      print('iteracion '+str(itera)+'\n'+pos)


